import React from 'react';

const Coin =({coin:{image, name, symbol, current_price, market_cap,price_change_percentage_24h}})=>{
    return(
        <div className="coin-container">
            <div className="coin-row">
                <div className="coin">
                    <img src={image} alt="crypto"/>
                    <h1>{name}</h1>
                    <p className="coin-symbol">{symbol}</p>
                </div>
                <div className="coin-data">
                    <p className="coin-price"> Rs.{current_price.toLocaleString()}</p>
                    <p className="coin-volume"> Rs.{market_cap.toLocaleString()}</p>
                    <p className ={(price_change_percentage_24h >0) ? "green " : "red "}>{price_change_percentage_24h.toFixed(4)}%</p>  
                </div>
            </div>

        </div>
    )
}

export default Coin;
